mapper.select:
select * from t_order a LEFT JOIN custom b ON a.customId=b.customId where b.customName like CONCAT(CONCAT('%',#{vo.customName}),'%')
AND time >= UNIX_TIMESTAMP(STR_TO_DATE(CONCAT(#{startDate}, ' 00:00:00'), '%Y-%m-%d %T')) AND time <= UNIX_TIMESTAMP(STR_TO_DATE(CONCAT(#{endDate}, ' 23:59:59'), '%Y-%m-%d %T'))
AND a.address like CONCAT(CONCAT('%',#{provinceStr}),'%') order by orderId limit #{page.currentPage},#{page.pageSize}
